--
-- Table structure for table `wp_posts`
--

CREATE TABLE `wp_posts` (
  `ID` bigint(20) UNSIGNED NOT NULL,
  `post_author` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_parent` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `wp_posts`
--

INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2020-08-07 06:09:39', '2020-08-07 06:09:39', '<!-- wp:paragraph -->\n<p>Welcome to WordPress. This is your first post. Edit or delete it, then start writing!</p>\n<!-- /wp:paragraph -->', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2020-08-07 06:09:39', '2020-08-07 06:09:39', '', 0, 'http://localhost/?p=1', 0, 'post', '', 1),
(2, 1, '2020-08-07 06:09:39', '2020-08-07 06:09:39', '<!-- wp:paragraph -->\n<p>This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>...or something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>As a new WordPress user, you should go to <a href=\"http://localhost/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!</p>\n<!-- /wp:paragraph -->', 'Sample Page', '', 'trash', 'closed', 'open', '', 'sample-page__trashed', '', '', '2020-08-07 06:43:57', '2020-08-07 06:43:57', '', 0, 'http://localhost/?page_id=2', 0, 'page', '', 0),
(3, 1, '2020-08-07 06:09:39', '2020-08-07 06:09:39', '<!-- wp:heading --><h2>Who we are</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Our website address is: http://localhost.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>What personal data we collect and why we collect it</h2><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Comments</h3><!-- /wp:heading --><!-- wp:paragraph --><p>When visitors leave comments on the site we collect the data shown in the comments form, and also the visitor&#8217;s IP address and browser user agent string to help spam detection.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>An anonymized string created from your email address (also called a hash) may be provided to the Gravatar service to see if you are using it. The Gravatar service privacy policy is available here: https://automattic.com/privacy/. After approval of your comment, your profile picture is visible to the public in the context of your comment.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Media</h3><!-- /wp:heading --><!-- wp:paragraph --><p>If you upload images to the website, you should avoid uploading images with embedded location data (EXIF GPS) included. Visitors to the website can download and extract any location data from images on the website.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Contact forms</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Cookies</h3><!-- /wp:heading --><!-- wp:paragraph --><p>If you leave a comment on our site you may opt-in to saving your name, email address and website in cookies. These are for your convenience so that you do not have to fill in your details again when you leave another comment. These cookies will last for one year.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>If you visit our login page, we will set a temporary cookie to determine if your browser accepts cookies. This cookie contains no personal data and is discarded when you close your browser.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>When you log in, we will also set up several cookies to save your login information and your screen display choices. Login cookies last for two days, and screen options cookies last for a year. If you select &quot;Remember Me&quot;, your login will persist for two weeks. If you log out of your account, the login cookies will be removed.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>If you edit or publish an article, an additional cookie will be saved in your browser. This cookie includes no personal data and simply indicates the post ID of the article you just edited. It expires after 1 day.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Embedded content from other websites</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Articles on this site may include embedded content (e.g. videos, images, articles, etc.). Embedded content from other websites behaves in the exact same way as if the visitor has visited the other website.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>These websites may collect data about you, use cookies, embed additional third-party tracking, and monitor your interaction with that embedded content, including tracking your interaction with the embedded content if you have an account and are logged in to that website.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Analytics</h3><!-- /wp:heading --><!-- wp:heading --><h2>Who we share your data with</h2><!-- /wp:heading --><!-- wp:heading --><h2>How long we retain your data</h2><!-- /wp:heading --><!-- wp:paragraph --><p>If you leave a comment, the comment and its metadata are retained indefinitely. This is so we can recognize and approve any follow-up comments automatically instead of holding them in a moderation queue.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>For users that register on our website (if any), we also store the personal information they provide in their user profile. All users can see, edit, or delete their personal information at any time (except they cannot change their username). Website administrators can also see and edit that information.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>What rights you have over your data</h2><!-- /wp:heading --><!-- wp:paragraph --><p>If you have an account on this site, or have left comments, you can request to receive an exported file of the personal data we hold about you, including any data you have provided to us. You can also request that we erase any personal data we hold about you. This does not include any data we are obliged to keep for administrative, legal, or security purposes.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Where we send your data</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Visitor comments may be checked through an automated spam detection service.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Your contact information</h2><!-- /wp:heading --><!-- wp:heading --><h2>Additional information</h2><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>How we protect your data</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>What data breach procedures we have in place</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>What third parties we receive data from</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>What automated decision making and/or profiling we do with user data</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Industry regulatory disclosure requirements</h3><!-- /wp:heading -->', 'Privacy Policy', '', 'trash', 'closed', 'open', '', 'privacy-policy__trashed', '', '', '2020-08-07 06:43:55', '2020-08-07 06:43:55', '', 0, 'http://localhost/?page_id=3', 0, 'page', '', 0),
(4, 1, '2020-08-07 06:09:52', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2020-08-07 06:09:52', '0000-00-00 00:00:00', '', 0, 'http://localhost/?p=4', 0, 'post', '', 0),
(5, 1, '2020-08-07 06:18:02', '2020-08-07 06:18:02', '<!-- wp:paragraph -->\n<p>Tension, nerve kitzel, an ultimate outdoor experience at dizzy heights awaits you in our high-ropes skyloft. Five courses with different levels of difficulty ensure the right kick. At a height between 8 and 15 meters you move from tree crown to tree crown, from one platform to the next. They balance beams, tree trunks, ropes and overcome swinging rope constructions. They mobilize unimaginable forces. Experience the world from the bird\'s eye view. Discover yourself and rely on your team. Skyloft is pure adventure.</p>\n<!-- /wp:paragraph -->', 'Start', '', 'publish', 'closed', 'closed', '', 'start', '', '', '2020-08-07 06:42:33', '2020-08-07 06:42:33', '', 0, 'http://localhost/?page_id=5', 0, 'page', '', 0),
(6, 1, '2020-08-07 06:17:48', '2020-08-07 06:17:48', '', 'klettern - seile', '', 'inherit', 'open', 'closed', '', 'klettern-seile', '', '', '2020-08-07 06:17:48', '2020-08-07 06:17:48', '', 5, 'http://localhost/wp-content/uploads/2020/08/Bild_17.jpg', 0, 'attachment', 'image/jpeg', 0),
(7, 1, '2020-08-07 06:18:02', '2020-08-07 06:18:02', '<!-- wp:paragraph -->\n<p>Tension, nerve kitzel, an ultimate outdoor experience at dizzy heights awaits you in our high-ropes skyloft. Five courses with different levels of difficulty ensure the right kick. At a height between 8 and 15 meters you move from tree crown to tree crown, from one platform to the next. They balance beams, tree trunks, ropes and overcome swinging rope constructions. They mobilize unimaginable forces. Experience the world from the bird\'s eye view. Discover yourself and rely on your team. Skyloft is pure adventure.</p>\n<!-- /wp:paragraph -->', 'Start', '', 'inherit', 'closed', 'closed', '', '5-revision-v1', '', '', '2020-08-07 06:18:02', '2020-08-07 06:18:02', '', 5, 'http://localhost/2020/08/07/5-revision-v1/', 0, 'revision', '', 0),
(8, 1, '2020-08-07 06:29:34', '2020-08-07 06:29:34', '<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":9,\"mediaType\":\"image\",\"mediaWidth\":25,\"imageFill\":true,\"focalPoint\":{\"x\":0.5,\"y\":0.75},\"className\":\"mb-3\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-image-fill mb-3\" style=\"grid-template-columns:auto 25%\"><figure class=\"wp-block-media-text__media\" style=\"background-image:url(http://localhost/wp-content/uploads/2020/08/Bild_08-686x1024.jpg);background-position:50% 75%\"><img src=\"http://localhost/wp-content/uploads/2020/08/Bild_08-686x1024.jpg\" alt=\"\" class=\"wp-image-9\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":3} -->\n<h3>You decide how high you are going.</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p> Start the mammoth tour, climb the Eagle Trail, choose the Silver Trail, the Magic Circle or the Flying Circus!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> In the Skyloft, you decide how far and how high you are going. Families with children, climbers, athletes, groups and companies are offered - depending on the degree - your experience of superlatives.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":13,\"mediaType\":\"image\",\"mediaWidth\":25,\"imageFill\":true,\"focalPoint\":{\"x\":0.2509754374279954,\"y\":0.5024594200981988},\"className\":\"mb-3\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-image-fill mb-3\" style=\"grid-template-columns:auto 25%\"><figure class=\"wp-block-media-text__media\" style=\"background-image:url(http://localhost/wp-content/uploads/2020/08/Bild_19-1024x683.jpg);background-position:25.097543742799537% 50.24594200981988%\"><img src=\"http://localhost/wp-content/uploads/2020/08/Bild_19-1024x683.jpg\" alt=\"\" class=\"wp-image-13\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":3} -->\n<h3>Family &amp; Leisure - The complete package!</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Magic Circus and Silver Trail - here families with children and beginners can gather their first high-rope experiences. Children can climb together with an adult already from 6 years and a body size of approx. 1,20 meters with us. Children up to 14 years of age are allowed to go through the parcours only if accompanied by an adult. For birthday parties, book an exciting afternoon with our barbecue at our log cabin. Schools, we offer a complete educational program with preparation and follow-up in the class community.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":12,\"mediaType\":\"image\",\"mediaWidth\":25,\"imageFill\":true,\"className\":\"mb-3\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-image-fill mb-3\" style=\"grid-template-columns:auto 25%\"><figure class=\"wp-block-media-text__media\" style=\"background-image:url(http://localhost/wp-content/uploads/2020/08/Bild_15-680x1024.jpg);background-position:50% 50%\"><img src=\"http://localhost/wp-content/uploads/2020/08/Bild_15-680x1024.jpg\" alt=\"\" class=\"wp-image-12\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":3} -->\n<h3>Companies &amp; Incentives - Finding teams together!</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>A day in the Skyloft promises a lot of fun and action, as well as many new, valuable experiences in dealing with colleagues, employees or friends.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> We offer targeted management training as well as unforgettable company incentives and train the team ability of your employees.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> Courage, self-confidence, flexibility and risk-tolerance are trained. But also communication, mutual trust, solution-oriented thinking and overcoming internal boundaries.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":10,\"mediaType\":\"image\",\"mediaWidth\":25,\"imageFill\":true} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-image-fill\" style=\"grid-template-columns:auto 25%\"><figure class=\"wp-block-media-text__media\" style=\"background-image:url(http://localhost/wp-content/uploads/2020/08/Bild_21-1024x685.jpg);background-position:50% 50%\"><img src=\"http://localhost/wp-content/uploads/2020/08/Bild_21-1024x685.jpg\" alt=\"\" class=\"wp-image-10\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":3} -->\n<h3>Proficlimber - Not for the faint hearted!</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Flying Circus, Mammoth Tour, Eagleshorst - these are our three trails for climbers and professionals. For those who love the thrill of kiting, we will take you on the wire rope in our ultimate \"Flying Circus\". Naturally well secured with belt and carabiner. The mammoth tour requires strength and endurance. Only professors can create our eagle horsemanship. Ten very difficult to climb elements at a height of 15 meters are the challenge. Access is only permitted from 16 years.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Offers', '', 'publish', 'closed', 'closed', '', 'offers', '', '', '2020-08-07 07:55:11', '2020-08-07 07:55:11', '', 0, 'http://localhost/?page_id=8', 0, 'page', '', 0),
(9, 1, '2020-08-07 06:21:21', '2020-08-07 06:21:21', '', 'Seilklettern', '', 'inherit', 'open', 'closed', '', 'seilklettern', '', '', '2020-08-07 06:21:21', '2020-08-07 06:21:21', '', 8, 'http://localhost/wp-content/uploads/2020/08/Bild_08.jpg', 0, 'attachment', 'image/jpeg', 0),
